import React, { useState, useEffect } from "react";
import Table from "react-bootstrap/Table";
import Container from "react-bootstrap/Container";
import { scheduleGetAllDays } from "../../../serverRelated/ApiRequest";

import { format, parseISO } from "date-fns";
import { fr } from "date-fns/locale"; // Importer les locales françaises

const getCurrentYear = () => {
  const currentDate = new Date();
  return currentDate.getFullYear();
};

export const ScheduleManagement = () => {
  const [preparedData, setPreparedData] = useState([]);

  useEffect(() => {
    const fetchData = async () => {
      const year = getCurrentYear();
      try {
        // Assurez-vous que les fonctions renvoient directement un tableau ou extrayez le tableau comme nécessaire
        const daysResponse = await scheduleGetAllDays(year);
        console.log("daysResponse", daysResponse);

        const preparedData = prepareDataForDisplay(daysResponse);
        setPreparedData(preparedData);
      } catch (error) {
        console.error("Erreur lors de la récupération des horaires:", error);
      }
    };

    fetchData();
  }, []);

  const prepareDataForDisplay = (data) => {
    const groupedData = [];
    let currentGroup = [];

    Object.keys(data).forEach((weekNumber) => {
      const week = data[weekNumber];
      // Pour chaque semaine, vérifiez si vous avez plus de 7 jours et ajustez en conséquence.
      week.forEach((day, index) => {
        if (index < 7) {
          // Assurez-vous de ne prendre que les 7 premiers jours
          currentGroup.push(day);
        }
      });

      if (currentGroup.length > 0) {
        groupedData.push(currentGroup);
        currentGroup = [];
      }
    });

    return groupedData;
  };

  const rowsHeaders = [
    "Date",
    "Ouverture Matin",
    "Fermeture Matin",
    "Ouverture Après-midi",
    "Fermeture Après-midi",
    "État", // Nouvel en-tête pour afficher l'état d'ouverture
  ];

  return (
    <>
      <Container>
        {preparedData.map((week, index) => (
          <Table
            key={index}
            striped
            bordered
            hover
          >
            <tbody>
              {rowsHeaders.map((header, headerIndex) => (
                <tr key={headerIndex}>
                  <th>{header}</th>
                  {week.map((day, dayIndex) => {
                    let cellContent;
                    switch (header) {
                      case "Date":
                        const date = parseISO(day.date); // Supposons que vous avez déjà importé parseISO et format
                        cellContent = format(date, "EEEE dd MMMM yyyy", {
                          locale: fr,
                        });
                        break;
                      case "Ouverture Matin":
                        cellContent = day.morningOpening || "Fermé";
                        break;
                      case "Fermeture Matin":
                        cellContent = day.morningClosing || "Fermé";
                        break;
                      case "Ouverture Après-midi":
                        cellContent = day.afternoonOpening || "Fermé";
                        break;
                      case "Fermeture Après-midi":
                        cellContent = day.afternoonClosing || "Fermé";
                        break;
                      case "État": // Nouvelle case pour gérer l'affichage de l'état d'ouverture
                        cellContent = day.isOpen ? "Ouvert" : "Fermé";
                        break;
                      default:
                        cellContent = "N/A";
                    }
                    return (
                      <td key={`${headerIndex}-${dayIndex}`}>{cellContent}</td>
                    );
                  })}
                </tr>
              ))}
            </tbody>
          </Table>
        ))}
      </Container>
    </>
  );
};

export default ScheduleManagement;
