import React, { useState, useEffect } from "react";
import { Accordion } from "react-bootstrap";
import Container from "react-bootstrap/Container";
import Form from "react-bootstrap/Form";
import OverlayTrigger from 'react-bootstrap/OverlayTrigger';
import Tooltip from 'react-bootstrap/Tooltip';



const capitalizeFirstLetter = (string) => {
  return string.charAt(0).toUpperCase() + string.slice(1);
};

const renderTooltip = (description) => (
    <Tooltip id="button-tooltip">
      {description}
    </Tooltip>
  ); 

const VehicleFilters = () => {
    const [brands, setBrands] = useState([]);
    const [vehicleModels, setVehicleModels] = useState([]);
    const [uniqueVehicleTypes, setUniqueVehicleTypes] = useState([]);
    const [selectedVehicleTypes, setSelectedVehicleTypes] = useState([]);
    const [vehicleTypes, setVehicleTypes] = useState([]);
    const [vehicleSubtypes, setVehicleSubtypes] = useState([]);
  
    // Fetch brands
    useEffect(() => {
      const fetchBrands = async () => {
        try {
          const response = await fetch("http://localhost:5001/api/brands");
          const data = await response.json();
          setBrands(data);
        } catch (error) {
          console.error("Error fetching brands:", error);
        }
      };
      fetchBrands();
    }, []);
  
    // Fetch vehicle models
    useEffect(() => {
      const fetchVehicleModels = async () => {
        try {
          const response = await fetch("http://localhost:5001/api/vehicleModels");
          const data = await response.json();
          setVehicleModels(data);
        } catch (error) {
          console.error("Error fetching VehicleModels:", error);
        }
      };
      fetchVehicleModels();
     }, []);
  
    // Fetch vehicle types and create a set of unique types
    useEffect(() => {
      const fetchVehicleTypes = async () => {
        try {
          const response = await fetch("http://localhost:5001/api/vehicleTypes");
          const data = await response.json();
          setVehicleTypes(data);
          const vehicleTypeNames = [...new Set(data.map((item) => item.vehicle_type_name))];
          setUniqueVehicleTypes(vehicleTypeNames);
        } catch (error) {
          console.error("Error fetching vehicleTypes:", error);
        }
      };
      fetchVehicleTypes();
    }, []);
  
    // Update the list of subtypes when the selected types change
    useEffect(() => {
      const filteredSubtypes = vehicleTypes.filter(type =>
        selectedVehicleTypes.includes(type.vehicle_type_name)
      );
      setVehicleSubtypes(filteredSubtypes);
    }, [selectedVehicleTypes, vehicleTypes]);
  
    // Handle vehicle type selection
    const handleVehicleTypeChange = (event) => {
      const typeValue = event.target.value;
      setSelectedVehicleTypes(prevSelectedTypes =>
        event.target.checked
          ? [...prevSelectedTypes, typeValue]
          : prevSelectedTypes.filter(type => type !== typeValue)
      );
    };
  

  return (
    <Container
      className="bg-dark filters-wrapper"
      style={{ width: "20%" }}
    >
      <Accordion
        defaultActiveKey={["0"]}
        style={{
          "--bs-accordion-bg": "transparent",
          "--bs-accordion-border-color": "transparent",
        }}
        alwaysOpen
      >
        <Accordion.Item eventKey="0">
          <Accordion.Header className="filter-accordion-title">
            Filtre
          </Accordion.Header>
          <Accordion.Body>
            {/* Types */}
            <Accordion
              defaultActiveKey={["0"]}
              style={{
                "--bs-accordion-bg": "transparent",
                "--bs-accordion-border-color": "transparent",
              }}
              alwaysOpen
            >
              <Accordion.Item eventKey="0">
                <Accordion.Header className="filter-accordion-sub-title">
                  Type de véhicule
                </Accordion.Header>
                <Accordion.Body>
                  <Form.Control
                    className="mb-3 filter-search-form  m-auto"
                    type="text"
                    placeholder="Search"
                  />
                  <Form className="px-3 filter-options-wrapper">
                    {uniqueVehicleTypes.map((type, index) => (
                      <Form.Check
                        key={index}
                        label={`${capitalizeFirstLetter(
                          type
                        )} (nb de véhicules)`}
                        value={type}
                        onChange={handleVehicleTypeChange}
                        className="text-light"
                      />
                    ))}
                  </Form>
                </Accordion.Body>
              </Accordion.Item>
            </Accordion>
            {/* SousTypes */}
            <Accordion
              defaultActiveKey={["0"]}
              style={{
                "--bs-accordion-bg": "transparent",
                "--bs-accordion-border-color": "transparent",
              }}
              alwaysOpen
            >
              <Accordion.Item eventKey="0">
                <Accordion.Header className="filter-accordion-sub-title">
                  Sous-type
                </Accordion.Header>
                <Accordion.Body>
                  <Form.Control
                    className="mb-3 filter-search-form  m-auto"
                    type="text"
                    placeholder="Search"
                  />
                  <Form className="px-3 filter-options-wrapper">
                    {vehicleSubtypes.map((subtype, index) => (
                      <Form.Check
                        key={index}
                        label={`${capitalizeFirstLetter(
                          subtype.vehicle_subtype_name
                        )} (nb de véhicules)`}
                        className="text-light"
                      />
                    ))}
                  </Form>
                </Accordion.Body>
              </Accordion.Item>
            </Accordion>

            {/* *Brand */}
            <Accordion
              defaultActiveKey={["0"]}
              style={{
                "--bs-accordion-bg": "transparent",
                "--bs-accordion-border-color": "transparent",
              }}
              alwaysOpen
            >
              <Accordion.Item eventKey="0">
                <Accordion.Header className="filter-accordion-sub-title">
                  Marques
                </Accordion.Header>
                <Accordion.Body>
                  <Form.Control
                    className="mb-3 filter-search-form  m-auto"
                    type="text"
                    placeholder="Search"
                  />
                  <Form className="px-3 filter-options-wrapper">
                    {brands.map((brand, index) => (
                      <Form.Check
                        key={index}
                        label={`${capitalizeFirstLetter(
                          brand.name
                        )} (nb de véhicules)`}
                        className="text-light"
                      />
                    ))}
                  </Form>
                </Accordion.Body>
              </Accordion.Item>
            </Accordion>

            {/* Models */}
            <Accordion
              defaultActiveKey={["0"]}
              style={{
                "--bs-accordion-bg": "transparent",
                "--bs-accordion-border-color": "transparent",
              }}
              alwaysOpen
            >
              <Accordion.Item eventKey="0">
                <Accordion.Header className="filter-accordion-sub-title">
                  Modèles
                </Accordion.Header>
                <Accordion.Body>
                  <Form.Control
                    className="mb-3 filter-search-form  m-auto"
                    type="text"
                    placeholder="Search"
                  />
                  <Form className="px-3 filter-options-wrapper">
                    {vehicleModels.map((vehicleModels, index) => (
                          <OverlayTrigger
                          key={index}
                          placement="right"
                          delay={{ show: 250, hide: 400 }}
                          overlay={renderTooltip(vehicleModels.description)} // Utilisez la description du modèle ici
                        >
                            <div>
                      <Form.Check
                        key={index}
                        label={`${vehicleModels.name} (nb)`}
                        className="text-light"
                      />
                        </div>
                        </OverlayTrigger>
                    ))}
                  </Form>
                </Accordion.Body>
              </Accordion.Item>
            </Accordion>
          </Accordion.Body>
        </Accordion.Item>
      </Accordion>
    </Container>
  );
};

export default VehicleFilters;
