import Card from "react-bootstrap/Card";
import Button from 'react-bootstrap/Button';
import Badge from "react-bootstrap/Badge";  
import carImage from '../img/toyota_aygoxxlimitedsu1b_angularfront.jpg';
import { Container } from 'react-bootstrap';

import OverlayTrigger from 'react-bootstrap/OverlayTrigger';
import Popover from 'react-bootstrap/Popover';

const popoverOptions = (
    <Popover id="popover-options">
      <Popover.Header as="h3" className="popover-title">Options et équipements</Popover.Header>
      <Popover.Body className="popover-text">
        
        <ul>
            <li>ABS</li>
            <li>Climatisation</li>
        </ul>
      </Popover.Body>
    </Popover>
  );

export const VehicleCard = () => {
  return (
      <Card style={{ width: '18rem', borderRadius: '0px' }} className="bg-light">
                <Container className="bg-primary vehicle-card-tag"> dzqd</Container>
                <Card.Img className="mt25" variant="top" src={carImage} />
                <Card.Body>
                    
                    <Card.Title className='text-primary vehicle-card-title'>Toyota - Aygox</Card.Title>
                    <Card.Text className='text-dark mb-0'>
                        2007 - 100 000 km 
                    </Card.Text> 
                    <Card.Text className='text-dark'>
                        Diesel - Manuelle 6
                    </Card.Text> 
                    <Container>
                    <OverlayTrigger trigger="click" placement="right" overlay={popoverOptions}>
                        <Button variant="success">Options et équipêments</Button>
                    </OverlayTrigger>
                    </Container>
                    <Container className= 'text-center vehicle-card-price '>
                       <h3> 28.000€   <Badge bg="secondary" className="align-top bg-none text-dark" style={{ fontSize: '0.5em'}}>TTC</Badge>
                       </h3>
                    </Container>
                    <div className=" d-flex flex-row-reverse">
                    <Button variant="primary">Détails</Button>
                    </div>
                </Card.Body>
    </Card>
    )
};

export default VehicleCard;
