import Table from "react-bootstrap/Table";
import Button from "react-bootstrap/Button";
import { useState, useEffect } from "react";
import { scheduleGetAllDays, scheduleUpdateDay, scheduleGetWeek} from "../../../serverRelated/ApiRequest";

export const ScheduleManagement = ({ weekData }) => {


  const getSchedule = async () => {
    try {
      const data = {days: [], week: []};
      data.days = await scheduleGetAllDays();
      data.week = await scheduleGetWeek();
    } catch (error) {
      

      
  };

  };
     

    useEffect(() => {
        const year = 2024;
        const weekNumber = 8;
        const weekStart = 10;
      
        scheduleGetWeek(weekStart, year, weekNumber).then(weekData => {
            console.log('Week Data:', weekData);
          });
      
        scheduleGetAllDays(year).then(dayData => {
          console.log('Day Data:', dayData);
        });
      }, []);
    
        const colsHeaders = ["Date", "Ouverture Matin",
         "Fermeture Matin", "Ouverture Après-midi", "Fermeture Après-midi"];

        return (
          <Table striped bordered hover>
            <thead>
              <tr>
                {colsHeaders.map((header, index) => (
                  <th key={index}>{header}</th>
                ))}
              </tr>
            </thead>
            <tbody>
              {Object.keys(weekData).map(weekNumber =>
                weekData[weekNumber].map((day, dayIndex) => (
                  <tr key={dayIndex}>
                    <td>{day.date}</td>
                    <td>{day.morningOpening || "Fermé"}</td>
                    <td>{day.morningClosing || "Fermé"}</td>
                    <td>{day.afternoonOpening || "Fermé"}</td>
                    <td>{day.afternoonClosing || "Fermé"}</td>
                  </tr>
                ))
              )}
            </tbody>
          </Table>
        );
      };

      
export default ScheduleManagement;
