import React, { useState, useEffect } from "react";
import { Button, Form } from "react-bootstrap";
import Select from "react-select";
import { capitalizeFirstLetter } from "../../../../functions/capitalizeFirstLetter";
import AddFuelModal from "../../../addingEntries/AddFuelModal";
// Renommez cette variable pour éviter les conflits
let initialFuelTypes = [
  // { value: "id", label: "FuelType name" },
];

export const FuelTypeForm = () => {
  const [showModal, setShowModal] = useState(false);
  const handleOpenModal = () => {
    setShowModal(true);
  };

  const handleCloseModal = () => setShowModal(false);

  const [selectedFuelType, setSelectedFuelType] = useState(null);
  const [fuelTypes, setFuelTypes] = useState(initialFuelTypes); // Utilisez le nom renommé ici

  useEffect(() => {
    const fetchFuelTypes = async () => {
      try {
        const response = await fetch("http://localhost:5001/api/fuelTypes");
        const data = await response.json();

        const transformedFuelTypes = data.map((fuelType) => ({
          value: fuelType.id.toString(),
          label: capitalizeFirstLetter(fuelType.name),
        }));

        setFuelTypes(transformedFuelTypes); // Modifiez pour remplacer complètement les marques
      } catch (error) {
        console.error("Error fetching options:", error);
      }
    };
    fetchFuelTypes();
  }, []);

  return (
    <>
      <Form.Label>
        <h3 className="key-info-title">Types :</h3>
      </Form.Label>
      <Select
        value={selectedFuelType}
        onChange={setSelectedFuelType}
        options={fuelTypes}
        isSearchable
      />
      <Button onClick={handleOpenModal}>Ajouter une marque</Button>
      <AddFuelModal
        show={showModal}
        onHide={handleCloseModal}
      />
    </>
  );
};

export default FuelTypeForm;
