import { Home } from "./screens/Home";
import { Vehicles } from "./screens/Vehicles";
import { Service } from "./screens/Service";
import { Admin } from "./screens/Admin";
import { Test } from "./screens/Test";
import { VehicleDetails } from "./screens/VehicleDetails";
import { ReactComponent as Logo } from "./visuals/brand/LOGO_FULL_LIGHT.svg";
import { FooterComp } from "../src/components/general/FooterComp";
import { VehicleDetailsForm } from "./screens/VehicleDetailsForm";

import "./styles/main.css";
import { Routes, Route, NavLink } from "react-router-dom";

const navLinkStyle = ({ isActive }) => ({
  margin: "0 100px",
  fontSize: "1.3em",
  fontFamily: "'Barlow Condensed', sans-serif",
  fontWeight: "bold",
  textTransform: "uppercase",
  textDecoration: "none",
  color: isActive ? "gray" : "#EA6D2A",
});

function App() {
  return (
    <>
        <div className="bg-image">
          <nav className="pt-5">
            <div className="container d-flex justify-content-around text-center">
              <NavLink
                to="/"
                style={navLinkStyle}
              >
                Nous <br /> Connaitre
              </NavLink>
              <NavLink
                to="/services-catalogue"
                style={navLinkStyle}
              >
                Nos <br />
                Services
              </NavLink>
              <Logo className="navbar-logo" />
              <NavLink
                to="/vehicles"
                style={navLinkStyle}
              >
                Nos <br />
                Vehicules
              </NavLink>
              <NavLink
                to="/test"
                style={navLinkStyle}
              >
                Nos
                <br />
                Details
              </NavLink>
            </div>
          </nav>
            <Routes>
              <Route
                path="/"
                element={<Home />}
              />
              <Route
                path="/services-catalogue"
                element={<Service />}
              />
              <Route
                path="/vehicles"
                element={<Vehicles />}
              />
              <Route
                path="/test"
                element={<Test />}
              />
              <Route
                path="/vehicleDetails/:vehicleId"
                element={<VehicleDetails />}
              />

              <Route
                path="/admin"
                element={<Admin />}
              />

              <Route
                path="/vehicleForm"
                element={<VehicleDetailsForm />}
              />
            </Routes>

        </div>
    <footer className="bg-light">

      <FooterComp/>

    </footer>
    </>
  );
}

export default App;
