import Card from "react-bootstrap/Card";
import Button from 'react-bootstrap/Button';
import carImage from '../img/toyota_aygoxxlimitedsu1b_angularfront.jpg';
import { Container } from 'react-bootstrap';

import OverlayTrigger from 'react-bootstrap/OverlayTrigger';
import Popover from 'react-bootstrap/Popover';

const popoverOptions = (
    <Popover id="popover-options">
      <Popover.Header as="h3" className="bg-dark ">Options et équipement</Popover.Header>
      <Popover.Body className="bg-dark">
        And here's some <strong>amazing</strong> content. It's very engaging.
        right?
      </Popover.Body>
    </Popover>
  );

export const VehicleCard = () => {
  return (
  <Card style={{ width: '18rem' }}>
                <Card.Img className="mt-5" variant="top" src={carImage} />
                <Card.Body>
                    <Card.Title className='text-primary vehicle-card-title'>Toyota - Aygox</Card.Title>
                    <Card.Text className='text-dark mb-0'>
                        2007 - 100 000 km 
                    </Card.Text> 
                    <Card.Text className='text-dark'>
                        Diesel - Manuelle 6
                    </Card.Text> 
                    <Container>
                    <OverlayTrigger trigger="click" placement="right" overlay={popoverOptions}>
                        <Button variant="success">Options et équipêments</Button>
                    </OverlayTrigger>
                    </Container>
                    <Container className= 'text-center vehicle-card-price'>
                        28.000€
                    </Container>
                    <div className=" d-flex flex-row-reverse">
                    <Button variant="primary">Détails</Button>
                    </div>
                </Card.Body>
    </Card>
    )
};

export default VehicleCard;
