import React, { useEffect, useState } from 'react';
import { scheduleGetWeek } from "../../serverRelated/ApiRequest";
import { Row, Col } from "react-bootstrap";
import { format, getDate } from 'date-fns';
import { fr } from 'date-fns/locale'; // Importez le locale de votre choix

const getCurrentDate = () => {
  const now = new Date();
  return {
    year: now.getFullYear(),
    weekNumber: now.getDate() - now.getDay() + (now.getDay() === 0 ? -6 : 1),
  };
};

export const FooterSchedule = () => {
  const [schedule, setSchedule] = useState([]);

  useEffect(() => {
    const fetchData = async () => {
      const currentDate = getCurrentDate();
      try {
        const year = currentDate.year;
        const weekNumber = currentDate.weekNumber;

        const response = await scheduleGetWeek(year, weekNumber, 1);
        const weekData = response[weekNumber];
        if (weekData) {
          const transformedWeekData = weekData.map(day => {
            // Détermine l'affichage pour le matin et l'après-midi
            const morningStatus = day.morningOpening === null || day.morningClosing === null ? "Fermé" : `${day.morningOpening.slice(0, -3)} - ${day.morningClosing.slice(0, -3)}`;
            const afternoonStatus = day.afternoonOpening === null || day.afternoonClosing === null ? "Fermé" : `${day.afternoonOpening.slice(0, -3)} - ${day.afternoonClosing.slice(0, -3)}`;
            return {
              ...day,
              morningStatus,
              afternoonStatus,
            };
          });
          setSchedule(transformedWeekData);
        } else {
          setSchedule([]);
        }
      } catch (error) {
        console.error("Erreur lors de la récupération des horaires:", error);
      }
    };

    fetchData();
  }, []);


  const getDate = (date) => {
    const dayDate = new Date(date);
    const dayName = format(dayDate, 'EEEE', { locale: fr });
    return `${dayName.charAt(0).toUpperCase() + dayName.slice(1)}`;
  }

  return (
    <div>
      {schedule.length > 0 ? (
        <Row>
          {schedule.map((day, index) => (
            <Col key={index}>
                <p>{getDate(day.date)}</p>
              <li className='footer-schedule'>Matin : <br/> {day.morningStatus}</li>
              <li>Après-midi : <br/> {day.afternoonStatus} </li>
            </Col>
            
          ))}
        </Row>
      ) : (
        <p>Aucun horaire disponible</p>
      )}
    </div>
  );
};

export default FooterSchedule;
