import React, { useState, useEffect } from "react";
import { Button, Form } from "react-bootstrap";
import Select from "react-select";
import { capitalizeFirstLetter } from "../functions/capitalizeFirstLetter";
import AddOptionModal from "../components/addingEntries/addOptionModal";


const vehicleOptions = [
 // { value: "addNewOption", label: "Ajouter une option de véhicule" },
];

export const Test = () => {
const [showModal, setShowModal] = useState(false);

  const handleOpenModal = () => setShowModal(true);
  const handleCloseModal = () => setShowModal(false);
  const [selectedOption, setSelectedOption] = useState(null);
  const [options, setOptions] = useState(vehicleOptions); // Initialisation avec vehicleOptions

  useEffect(() => {
    const fetchOptions = async () => {
      try {
        const response = await fetch("http://localhost:5001/api/options");
        const data = await response.json();

        // Transformer les données reçues pour qu'elles correspondent au format de vehicleOptions
        const transformedOptions = data.map(option => ({
          value: option.id.toString(), // Assurez-vous que la valeur est une chaîne
          label: capitalizeFirstLetter(option.name),
        }));

        setOptions(prevOptions => [...prevOptions, ...transformedOptions]);
      } catch (error) {
        console.error("Error fetching options:", error);
      }
    };
    fetchOptions();
  }, []);

  return (
    <>
      <Form.Label>
        <h3 className="key-info-title">Types :</h3>
      </Form.Label>
      <Select
        value={selectedOption}
        onChange={setSelectedOption}
        options={options}
        isMulti
        isSearchable
      />

<Button 
variant="primary"
onClick={handleOpenModal}>Ajouter une option</Button>

<AddOptionModal show={showModal} onHide={handleCloseModal} />
    </>

  );
};

export default Test;
