import React, { useState, useEffect } from "react";
import { fetchDisplayedTestimonials } from "../serverRelated/ApiRequest";
import { handleDateDifference } from "../functions/handleDateDifference";
import ratingStars from "../functions/ratingStars";
import StarsIcons from "../components/general/StarsIcons";

export const TestimonySect = () => {
  const [testimonials, setTestimonials] = useState([]);

  useEffect(() => {
    const getTestimonials = async () => {
      try {
        const data = await fetchDisplayedTestimonials();
        setTestimonials(data);
      } catch (error) {
        console.error("Erreur lors de la récupération des témoignages :", error);
      }
    };

    getTestimonials();
  }, []);

  return (
    <>
      {testimonials.map((testimony, index) => (
        console.log(testimony),
        <div key={index} className="bg-light testimony-card text-center m-auto">
          <p>Note: {ratingStars(testimony.note)}</p>
          <p>{testimony.testimony}</p>
          <p>{testimony.name}</p>
          <p>Il y a {handleDateDifference(testimony.posted_at)}</p>
        </div>
      ))}
    </>
  );
};
