import React from 'react';

import StarsIcons from '../components/general/StarsIcons';

function ratingStars(props) {
  const { rating } = props;
  <StarsIcons.Full /> 
  // Fonction pour calculer les étoiles en fonction du rating
  const calculateStars = (rating) => {
    const fullStars = Math.floor(rating);
    const hasHalfStar = rating % 1 !== 0;
    const emptyStars = 5 - fullStars - (hasHalfStar ? 1 : 0);

    return { fullStars, hasHalfStar, emptyStars };
  };

  const { fullStars, hasHalfStar, emptyStars } = calculateStars(rating);

  // Génère les étoiles en utilisant les composants SVG importés
  const renderStars = () => {
    const stars = [];

    for (let i = 0; i < fullStars; i++) {
      stars.push(<StarsIcons.Full key={i} />);
    }

    if (hasHalfStar) {
      stars.push(<StarsIcons.Half key="half" />);
    }

    for (let i = 0; i < emptyStars; i++) {
      stars.push(<StarsIcons.Empty key={`empty${i}`} />);
    }

    return stars;
  };

  return <Row className="rating-stars">{renderStars()}</Row>;
}

export default ratingStars;
