import React, { useEffect, useState } from 'react';
import { scheduleGetWeek } from "../../serverRelated/ApiRequest";
import { Container, Row, Col } from "react-bootstrap";
import { format } from 'date-fns';
import { fr } from 'date-fns/locale'; // Importez le locale de votre choix

const getCurrentDate = () => {
  const now = new Date();
  return {
    year: now.getFullYear(),
    weekNumber: now.getDate() - now.getDay() + (now.getDay() === 0 ? -6 : 1),
  };
};

export const FooterSchedule = () => {
  const [schedule, setSchedule] = useState([]);

  useEffect(() => {
    const fetchData = async () => {
      const currentDate = getCurrentDate();
      try {
        const year = currentDate.year;
        const weekNumber = currentDate.weekNumber + 1;

        const response = await scheduleGetWeek(year, weekNumber, 1);
        const weekData = response[weekNumber];
        console.log(weekData);
        if (weekData) {
          const transformedWeekData = weekData.map(day => {
            const isMorningClosed = day.morningOpening === null || day.morningClosing === null;
            const isAfternoonClosed = day.afternoonOpening === null || day.afternoonClosing === null;
            const dayStatus = isMorningClosed && isAfternoonClosed ? "Fermé pour toute la journée" : null;

            return {
              ...day,
              dayStatus,
              morningStatus: !isMorningClosed ? `${day.morningOpening.slice(0, -3)} - ${day.morningClosing.slice(0, -3)}` : "Fermé",
              afternoonStatus: !isAfternoonClosed ? `${day.afternoonOpening.slice(0, -3)} - ${day.afternoonClosing.slice(0, -3)}` : "Fermé",
            };
          });
          setSchedule(transformedWeekData);
        } else {
          setSchedule([]);
        }
      } catch (error) {
        console.error("Erreur lors de la récupération des horaires:", error);
      }
    };

    fetchData();
  }, []);

  const getDate = (date) => {
    const dayDate = new Date(date);
    const dayName = format(dayDate, 'EEEE', { locale: fr });
    return `${dayName.charAt(0).toUpperCase() + dayName.slice(1)}`;
  }

  // Séparez le tableau en deux : la majorité des jours et les deux derniers jours
  const mainSchedule = schedule.slice(0, -2);
  const lastTwoDays = schedule.slice(-2);

  return (
    <Container>
      {schedule.length > 0 ? (
        <> <p className="footer-schedule pb-2 border-light">Horaires d'ouverture</p>
          <Row>
            {mainSchedule.map((day, index) => (
              <Col key={index}>
                  <p className='schedule-days'>{getDate(day.date)}</p>
                  {day.dayStatus ? (
                    <p className='footer-schedule schedule-hours'>{day.dayStatus}</p>
                  ) : (
                    <>
                      <p  className='schedule-hours' >Matin : <br/> {day.morningStatus}</p>
                      <p  className='schedule-hours'>Après-midi : <br/> {day.afternoonStatus}</p>
                    </>
                  )}
              </Col>
            ))}
          </Row>
          <Row className="mt-4"> 
            {lastTwoDays.map((day, index) => (
              <Col key={index}>
                  <p  className='schedule-days'>{getDate(day.date)}</p>
                  {day.dayStatus ? (
                    <p className='footer-schedule schedule-hours'>{day.dayStatus}</p>
                  ) : (
                    <>
                      <p   className='schedule-hours'>Matin : <br/> {day.morningStatus}</p>
                      <p  className='schedule-hours'>Après-midi : <br/> {day.afternoonStatus}</p>
                    </>
                  )}
              </Col>
            ))}
          </Row>
        </>
      ) : (
        <p>Aucun horaire disponible</p>
      )}
    </Container>
  );
};

export default FooterSchedule;
