import { capitalizeFirstLetter } from "../../functions/capitalizeFirstLetter";
import React, { useState, useEffect } from "react";
import { Modal, Button } from 'react-bootstrap';
import Select from "react-select";
import { Form } from "react-bootstrap";
import CreatableSelect from 'react-select/creatable';

const AddOptionModal = ({ show, onHide }) => {

    let initialOptions = [
        // { value: "id", label: "Option name" },
    ];

    const [selectedOption, setSelectedOption] = useState(null);
    const [options, setOptions] = useState(initialOptions); // Utilisez le nom renommé ici


    useEffect(() => {
        const fetchOptions = async () => {
            try {
                const response = await fetch("http://localhost:5001/api/options");
                const data = await response.json();

                const transformedOptions = data.map(option => ({
                    value: option.id.toString(),
                    label: capitalizeFirstLetter(option.name),
                }));

                setOptions(transformedOptions); 
            } catch (error) {
                console.error("Error fetching options:", error);
            }
        };
        fetchOptions();
    }, []);

    const handleAddOption = async () => {
        try {
            const response = await fetch("http://localhost:5001/api/options", {
                method: "POST",
                headers: {
                    "Content-Type": "application/json",
                },
                body: JSON.stringify({
                    name: selectedOption.label,
                }),
            });
            const data = await response.json();
            console.log(data);
        } catch (error) {
            console.error("Error adding option:", error);
        }
    }

  return (
    <Modal show={show} onHide={onHide}>
      <Modal.Header closeButton>
        <Modal.Title>Titre du modal</Modal.Title>
      </Modal.Header>
      <Modal.Body>
      <Form.Label>
        <h3 className="key-info-title">Option et équipements :</h3>
      </Form.Label>
      <CreatableSelect isClearable is isSearchable onChange={setSelectedOption}
        options={options} />

<Form.Group className="mb-3" controlId="exampleForm.ControlTextarea1">
        <Form.Label>Example textarea</Form.Label>
        <Form.Control as="textarea" rows={3} />
      </Form.Group>
      </Modal.Body>
      <Modal.Footer>
      <Modal.Footer>
        <Button variant="primary" onClick={handleAddOption}>Ajouter</Button>
        <Button variant="orange" onClick={handleModifyOption}>Modifier</Button>
        <Button variant="danger" onClick={() => handleDeleteOption(selectedOption?.value)}>Supprimer</Button>
        <Button variant="secondary" onClick={onHide}> Fermer </Button>
      </Modal.Footer>
      </Modal.Footer>
    </Modal>
  );
};

export default AddOptionModal;
