import React, { useState, useEffect } from "react";
import Table from "react-bootstrap/Table";
import Button from "react-bootstrap/Button";
import { fetchCustomerRequest } from "../../../serverRelated/ApiRequest";
import { formatDateToDmy } from '../../../functions/formatDateToDmy';
import VehicleDetails from "../../VehicleDetails";
import { useNavigate } from "react-router-dom";

export const RequestManagement = () => {
  const [requests, setRequests] = useState([]);

  useEffect(() => {
    const getCustomerRequests = async () => {
      try {
        const data = await fetchCustomerRequest();
        setRequests(data);
      } catch (error) {
        console.error(
          "Erreur lors de la récupération des demandes client:",
          error
        );
      }
    };
    

    getCustomerRequests();
  }, []);

  const navigate = useNavigate();
    const handleOpenDetails = () => {
      navigate(`/vehicleDetails/${request.vehicle_id}`);
    };

  return (
    <>
      <div className="bg-dark">
        <h1>RequestManagement</h1>

        <Table
          striped
          bordered
          hover
        >
          <thead>
            <tr>
              <th>Reçu il y a</th>
              <th>Statut</th>
              <th>Request ref</th>
              <th>Nom du client</th>
              <th>Vehicle ref</th>

              <th>Message</th>
              <th>Numéro de téléphone</th>
              <th>Email</th>
              <th>Préférence</th>
              <th>Actions</th>
            </tr>
          </thead>
          <tbody>
            {requests.map((request, index) => (
              <tr key={index}>
                <td>{formatDateToDmy(request.received_at)}</td>
                <td>{request.is_processed ? "Traitée" : "En attente"}</td>
                <td>{request.id}</td>
                <td>{request.requester_name}</td>
                <td><Button
                  onClick={handleOpenDetails}
                > </Button></td>
                <td>{request.message}</td>
                <td>{request.phone}</td>
                <td>{request.email}</td>
                <td>{request.preferPhone ? "Téléphone" : "Email"}</td>
                <td>
                  <button className="btn btn-primary">Traitée</button>
                  <button className="btn btn-danger">Supprimer</button>
                </td>
              </tr>
            ))}
          </tbody>
        </Table>
        <VehicleDetails
        
        />

      </div>


    </>
  );
};

export default RequestManagement;
