import React, { useState, useEffect } from 'react';
import { Accordion, Card, useAccordionToggle } from 'react-bootstrap';
import Container from 'react-bootstrap/Container';
import Form from 'react-bootstrap/Form';
import OverlayTrigger from 'react-bootstrap/OverlayTrigger';
import Tooltip from 'react-bootstrap/Tooltip';

const capitalizeFirstLetter = (string) => {
    return string.charAt(0).toUpperCase() + string.slice(1);
  }

const VehicleFilters = () => {

    const [brands, setBrands] = useState([]);
    useEffect(() => {
      const fetchBrands = async () => {
        try {
          const response = await fetch('http://localhost:5001/api/brands');
          const data = await response.json();
          setBrands(data);
        } catch (error) {
          console.error("Error fetching brands:", error);
        }
      };
  
      fetchBrands();
    }, []);

    const [vehicleModels, setVehicleModels] = useState([]);
    useEffect(() => {
      const fetchVehicleModels = async () => {
        try {
          const response = await fetch('http://localhost:5001/api/vehicleModels');
          const data = await response.json();
          setVehicleModels(data);
        } catch (error) {
          console.error("Error fetching VehicleModels:", error);
        }
      };
  
      fetchVehicleModels();
    }, []);
  

    const [uniqueVehicleTypes, setUniqueVehicleTypes] = useState([]);
    useEffect(() => {
      const fetchVehicleTypes = async () => {
        try {
          const response = await fetch('http://localhost:5001/api/vehicleTypes');
          const data = await response.json();
          
          // Créer un Set à partir des vehicle_type_name pour éliminer les doublons
          const vehicleTypeNames = [...new Set(data.map(item => item.vehicle_type_name))];
          setUniqueVehicleTypes(vehicleTypeNames);
        } catch (error) {
          console.error("Error fetching vehicleTypes:", error);
        }
      };
      fetchVehicleTypes();
    }, []);

    
    const [selectedVehicleType, setSelectedVehicleType] = useState('');
    const [vehicleTypes, setVehicleTypes] = useState([]);
    const [vehicleSubtypes, setVehicleSubtypes] = useState([]);

  useEffect(() => {
    const fetchVehicleTypes = async () => {
      try {
        const response = await fetch('http://localhost:5001/api/vehicleTypes');
        const data = await response.json();
        setVehicleTypes(data);
      } catch (error) {
        console.error("Error fetching vehicleTypes:", error);
      }
    };

    fetchVehicleTypes();
  }, []);

  const handleVehicleTypeChange = (event) => {
    const selectedType = event.target.value;
    setSelectedVehicleType(selectedType);
    // Filtrez les sous-types basés sur le type sélectionné
    const filteredSubtypes = vehicleTypes.filter((type) => type.vehicle_type_name === selectedType);
    setVehicleSubtypes(filteredSubtypes);
  };



  return (

    <Container className="bg-dark filters-wrapper" style={{ width: '20%'}}>
    <Accordion defaultActiveKey={['0']} style={{ '--bs-accordion-bg': 'transparent',
     '--bs-accordion-border-color': 'transparent'}} alwaysOpen>
      <Accordion.Item eventKey="0" >
        <Accordion.Header className='filter-accordion-title'>Filtre</Accordion.Header>
        <Accordion.Body>
       
           
            {/* Types */}
              <Accordion defaultActiveKey={['0']} style={{ '--bs-accordion-bg': 'transparent', '--bs-accordion-border-color': 'transparent'}} alwaysOpen>
      <Accordion.Item eventKey="0">
        <Accordion.Header className='filter-accordion-sub-title'>Type de véhicule</Accordion.Header>
        <Accordion.Body>
          <Form.Control className='mb-3 filter-search-form  m-auto' type="text" placeholder="Search" />
          <Form className='px-3 filter-options-wrapper'>
            {uniqueVehicleTypes.map((type, index) => (
              <Form.Check
                key={index}
                label={`${capitalizeFirstLetter(type)} (nb de véhicules)`}
                value={type}
                onChange={handleVehicleTypeChange}
                className='text-light'
              />
            ))}
          </Form>
        </Accordion.Body>
      </Accordion.Item>
    </Accordion>
             {/* SousTypes */}
             <Accordion defaultActiveKey={['0']} style={{ '--bs-accordion-bg': 'transparent',
            '--bs-accordion-border-color': 'transparent'}} alwaysOpen>
             <Accordion.Item eventKey="0">
                <Accordion.Header className='filter-accordion-sub-title'>Sous-type</Accordion.Header>
                <Accordion.Body>
                    <Form.Control className='mb-3 filter-search-form  m-auto' type="text" placeholder="Search" />
                    <Form className='px-3 filter-options-wrapper'>
                    {vehicleSubtypes.map((subtype, index) => (
  <Form.Check
    key={index}
    label={`${capitalizeFirstLetter(subtype.vehicle_subtype_name)} (nb de véhicules)`}
    className='text-light'
  />
))}
                    </Form>
                </Accordion.Body>
                </Accordion.Item>
            </Accordion>

            {/* *Brand */}
            <Accordion defaultActiveKey={['0']} style={{ '--bs-accordion-bg': 'transparent',
            '--bs-accordion-border-color': 'transparent'}} alwaysOpen>
             <Accordion.Item eventKey="0">
                <Accordion.Header className='filter-accordion-sub-title'>Marques</Accordion.Header>
                <Accordion.Body>
                    <Form.Control className='mb-3 filter-search-form  m-auto' type="text" placeholder="Search" />
                    <Form className='px-3 filter-options-wrapper'>
                    {brands.map((brand, index) => (
                        <Form.Check
                        key={index}
                        label={`${capitalizeFirstLetter(brand.name)} (nb de véhicules)`}
                        className='text-light'
                        />
                    ))}
                    </Form>
                </Accordion.Body>
                </Accordion.Item>
            </Accordion>

            {/* Models */}
            <Accordion defaultActiveKey={['0']} style={{ '--bs-accordion-bg': 'transparent',
            '--bs-accordion-border-color': 'transparent'}} alwaysOpen>
            <Accordion.Item eventKey="0" >
              
                <Accordion.Header className='filter-accordion-sub-title'>Modèles</Accordion.Header>
                <Accordion.Body>
                    <Form.Control className='mb-3 filter-search-form  m-auto' type="text" placeholder="Search" />
                    <Form className='px-3 filter-options-wrapper'>
                    {vehicleModels.map((vehicleModels, index) => (
                        <Form.Check
                        key={index}
                        label={`${vehicleModels.name} (nb)`}
                        className='text-light'
                        />
                    ))}
                    </Form>
                </Accordion.Body>
            </Accordion.Item>
            </Accordion>
        </Accordion.Body>
      </Accordion.Item>
    </Accordion>
    </Container>
    );
}; 

export default VehicleFilters;
