import { Home } from './screens/Home';
import { Vehicle } from './screens/Vehicle';
import { Service } from './screens/Service';
import { ReactComponent as Logo } from './visuals/brand/LOGO_FULL_LIGHT.svg';

import './styles/main.css';
import {Routes, Route, NavLink} from 'react-router-dom';

const navLinkStyle = ({ isActive }) => ({
  margin: '0 100px',
  fontSize: '1.3em',
  fontFamily: "'Barlow Condensed', sans-serif",
  fontWeight: 'bold',
  textTransform: 'uppercase',
  textDecoration: 'none',
  color: isActive ? 'gray' : '#EA6D2A', 
});

function App() {
  return (
    <div className='bg-image'>

       <header>
            <nav>
                <div className='container d-flex justify-content-around text-center'>
                <NavLink to='/' 
                style={navLinkStyle}>Nous <br/> Connaitre</NavLink>

                <NavLink to='/services' 
                 style={navLinkStyle}>Nos <br/>Services</NavLink>
                <Logo className='navbar-logo'/>
                <NavLink to='/vehicles' 
                 style={navLinkStyle}>Nos <br/>Vehicules</NavLink>
                <NavLink to='/vehicles' 
                 style={navLinkStyle}>Nos<br/>Vehicules</NavLink>
                </div>
              </nav>
              <Routes>
              <Route path="/" element={<Home/>}/>
              <Route path="/services" element={<Service/>}/>
              <Route path="/vehicles" element={<Vehicle/>}/>
            </Routes>
        </header>
      <div className="App">

    </div>
  </div>
  );
}

export default App;
