import Table from "react-bootstrap/Table";
import Button from "react-bootstrap/Button";
import { useState, useEffect } from 'react';
import { fetchServices, modifyService, deleteService } from "../../../serverRelated/ApiRequest";
import { Form } from "react-bootstrap";

export const ServiceManagement = () => {
    const [services, setServices] = useState([]);
    const [addingNewService, setAddingNewService] = useState(false);

    useEffect(() => {
        const getServices = async () => {
            const data = await fetchServices();
            setServices(data);
        };
        getServices();
    }, []);

    const handlePrepAddNewService = () => {
        setAddingNewService(true);
        setServices([{ id: null, name: '', description: '', min_price: '' }, ...services]);
    };

  const handleModifyService = async (id) => {
    const serviceToModify = services.find((service) => service.id === id);
    if (serviceToModify) {
      const updatedService = await modifyService(id, serviceToModify);
      setServices(services.map((service) => (service.id === id ? updatedService : service)));
    }
  };

  const handleAddNewService = async () => {}

  const handleDeleteService = async (id) => {
    await deleteService(id);
    setServices(services.filter((service) => service.id !== id));
  };

  return (
    <>
      <div className="bg-dark">
        <h1>ServiceManagement</h1>
      </div>

      <Button onClick={handlePrepAddNewService }>
        Ajouter un nouveau service
      </Button>

      <Table striped bordered hover>
        <thead>
          <tr>
            <th>Nom</th>
            <th>Description</th>
            <th>Prix minimum</th>
            <th>Actions</th>
          </tr>
        </thead>
        <tbody>
          {services.map((service, index) => (
            <tr key={index}>
              <td>
                <Form.Control
                  defaultValue={service.name}
                  type="text"
                  onChange={(e) => (service.name = e.target.value)}
                />
              </td>
              <td>
                <Form.Control
                  defaultValue={service.description}
                  type="text"
                  onChange={(e) => (service.description = e.target.value)}
                />
              </td>
              <td>
                <Form.Control
                  defaultValue={service.min_price}
                  type="number"
                  min="0"
                  onChange={(e) => (service.min_price = e.target.value)}
                />
              </td>
              <td>
                {service.id ? (
                  <>
                    <Button onClick={() => handleModifyService(service.id)}>
                      Appliquer les modifications
                    </Button>
                    <Button variant="danger" onClick={() => handleDeleteService(service.id)}>
                      Supprimer
                    </Button>
                  </>
                ) : (
                  <Button variant="success" onClick={() => handleAddNewService()}>
                    Cr√©er nouveau service
                  </Button>
                )}
              </td>
            </tr>
          ))}
        </tbody>
      </Table>
    </>
  );
};

export default ServiceManagement;
