import React, { useState, useEffect } from "react";
import { Button, Form } from "react-bootstrap";
import Select from "react-select";
import { capitalizeFirstLetter } from "../../../../functions/capitalizeFirstLetter";
import AddOptionModal from "../../../addingEntries/AddOptionModal";

// Variable renommée pour éviter les conflits
let initialOptions = [
  // Ajoutez une option pour "Ajouter une option de véhicule"
  { value: "addNewOption", label: "Ajouter une option de véhicule" },
];

export const OptionsForm = ({ onOptionsChange }) => {
  const [showModal, setShowModal] = useState(false);
  const handleOpenModal = () => setShowModal(true);
  const handleCloseModal = () => setShowModal(false);
  const [selectedOption, setSelectedOption] = useState(null);
  const [options, setOptions] = useState(initialOptions); // Utilisez le nom renommé ici

  useEffect(() => {
    const fetchOptions = async () => {
      try {
        const response = await fetch("http://localhost:5001/api/options");
        const data = await response.json();

        // Transformer les données reçues pour qu'elles correspondent au format de initialOptions
        const transformedOptions = data.map((option) => ({
          value: option.id.toString(), // Assurez-vous que la valeur est une chaîne
          label: capitalizeFirstLetter(option.name),
        }));

        // Ajoutez l'option "Ajouter une option de véhicule" aux options transformées
        transformedOptions.unshift({
          value: "addNewOption",
          label: "Ajouter une option de véhicule",
        });

        setOptions(transformedOptions);
      } catch (error) {
        console.error("Error fetching options:", error);
      }
    };
    fetchOptions();
  }, []);

// Dans le composant parent
const handleOptionsChange = (selectedOptions) => {
  // Faites quelque chose avec les options sélectionnées ici
  console.log("Options sélectionnées :", selectedOptions);
};

// Dans le rendu du composant parent


  return (
    <>
      <Select
        value={selectedOption}
        onChange={(selectedOptions) => {
          setSelectedOption(selectedOptions);
          onOptionsChange(selectedOptions); // Appeler le callback du parent avec les options sélectionnées
        }}
        options={options}
        isMulti
        isSearchable
      />
<OptionsForm onOptionsChange={handleOptionsChange} />

      <Button variant="primary" onClick={handleOpenModal}>
        Ajouter une option
      </Button>

      <AddOptionModal show={showModal} onHide={handleCloseModal} />
    </>
  );
};

export default OptionsForm;
