import React, { useState } from "react";
import Button from "react-bootstrap/Button";
import Form from "react-bootstrap/Form";
import axios from "axios"; // Assurez-vous d'installer axios avec `npm install axios`
import Modal from "react-bootstrap/Modal";


export const AdminConnexionPanel = ({ show, onHide}) => {
  const [email, setEmail] = useState(""); // État pour l'email
  const [password, setPassword] = useState(""); // État pour le mot de passe
  const handleSubmit = async (event) => {
    event.preventDefault(); // Empêche le comportement par défaut du formulaire
    try {
  
      const response = await fetch("http://localhost:5001/api/admins/login", {
        method: "POST",
        headers: {
          "Content-Type": "application/json",
        },
        body: JSON.stringify({ email, password })
      });
      
      if (response.ok) {
        // Si la connexion est réussie
        const data = await response.json();
        // Stockez le token et/ou d'autres informations nécessaires ici
        // Redirigez vers la page spéciale
        history.push('/cheminVersPageSpéciale');
      } else {
        // Gérez les erreurs de connexion ici
        console.error("Erreur de connexion");
      }
    } catch (error) {
      console.error(
        "Erreur de connexion : ",
        error.response ? error.response.data : error.message
      );
    }
  };


  return (
    <Modal show={show} onHide={onHide}>
    <Modal.Header closeButton>
      <Modal.Title>Connexion</Modal.Title>
    </Modal.Header>
    <Modal.Body>
    
    <Form onSubmit={handleSubmit}>
      <Form.Group
        className="mb-3"
        controlId="formBasicEmail"
      >
        <Form.Label>Email address</Form.Label>
        <Form.Control
          type="email"
          placeholder="Enter email"
          value={email}
          onChange={(e) => setEmail(e.target.value)} 
        />
      </Form.Group>
      <Form.Group
        className="mb-3"
        controlId="formBasicPassword"
      >
        <Form.Label>Password</Form.Label>
        <Form.Control
          type="passwonprd"
          placeholder="Password"
          value={password}
          onChange={(e) => setPassword(e.target.value)} 
        />
      </Form.Group>
      <Button
        variant="primary"
        type="submit"
      >
     
        Submit
      </Button>
    </Form>



    </Modal.Body>
    <Modal.Footer>
      <Button variant="secondary" onClick={onHide}> Fermer </Button>
    </Modal.Footer>
  </Modal>
    
  );

}
export default AdminConnexionPanel;
