import React, { useState, useEffect } from "react";
import { Container } from "react-bootstrap";
import GUIIcons from "../../general/GUIIcons";
import Offcanvas from "react-bootstrap/Offcanvas";
import { capitalizeFirstLetter } from "../../../functions/capitalizeFirstLetter";
import UserRequest from "../UserRequest";
import { Tabs, Tab } from "react-bootstrap";

function StickyDetails({ brand, vehicleModel, productionYear, mileage, fuelType, transmission, vehicleCondition, price, vehicleComment, vehicleModelDescription }) {
  const [topValue, setTopValue] = useState(0);

  const handleScroll = () => {
      setTopValue(window.scrollY);
  };

  useEffect(() => {
    window.addEventListener("scroll", handleScroll);

    return () => {
      window.removeEventListener("scroll", handleScroll);
    };
  }, []);

  const handlePhoneChange = (event) => {
    let input = event.target.value;
    if (input.startsWith("+336")) {
      input = "+336" + input.slice(4).replace(/[^0-9]/g, "");
    } else {
      input = "+336";
    }

    if (input.length > 13) {
      input = input.slice(0, 13);
    }
    setPhoneNumber(input);
  };

  //OffCanvas
  const [show, setShow] = useState(false);

  const handleClose = () => setShow(false);
  const handleShow = () => setShow(true);

  const [vehicleConditions, setVehicleConditions] = useState([]);
  useEffect(() => {
    const fetchVehicleConditions = async () => {
      try {
        const response = await fetch(
          "http://localhost:5001/api/vehicleConditions"
        );
        const data = await response.json();
        setVehicleConditions(data);
      } catch (error) {
        console.error("Error fetching vehicle conditions:", error);
      }
    };
    fetchVehicleConditions();
  }, []);

  return (
    <>
      <div
        style={{
          position: "absolute",
          top: `${topValue}px`,
          left: "1000px",
          width: "500px",
          height: "auto",
          transition: "all 0.5s ease",
        }}
        className="stick-info-wrapper"
      >
        <Container className="mt-2 mb-3">
          <h3 className="stick-info-title">{brand} - {vehicleModel}</h3>
          <p className="stick-infos">{productionYear} - {mileage} km</p>
          <p className="stick-infos">{fuelType} - {transmission} </p>
          <p
            className="stick-infos align-items-top"
            onClick={handleShow}
          >
            {" "}
            {vehicleCondition} <GUIIcons.Info className="info-icon" />
          </p>
        </Container>
        <div className="mb-5">
          <Tabs
            defaultActiveKey="condition-description"
            id="sticky-descriptions"
            className="bg-dark mt-4"
            fill
          >
            <Tab
              eventKey="condition-description"
              title="État du véhicule"
              className="bg-light pb-3 pt-3 px-3"
            >
              {vehicleComment}
            </Tab>
            <Tab
              eventKey="model-description"
              title="Commentaire du Modèle"
              className="bg-light pb-3 pt-3 px-3"
            >
             {vehicleModelDescription}
            </Tab>
          </Tabs>
        </div>
        <div className="d-flex flex-column justify-content-center align-items-center">
          <h4 className="stick-info-garantie">Garantie 12 mois</h4>
          <h3 className="stick-info-price">{price} €</h3>
          <h4 className="stick-info-garantie mt-4">
            Une question ? Un rendez-vous ?
          </h4>

        </div>
      </div>

      <Offcanvas
        show={show}
        onHide={handleClose}
        className="bg-dark custom-offcanvas"
      >
        <Offcanvas.Header
          closeVariant="white"
          closeButton
        >
          <Offcanvas.Title className="mx-auto offcanvas-title">
            États des véhicules
          </Offcanvas.Title>
        </Offcanvas.Header>
        <Offcanvas.Body>
          <ul className="px-1">
            {vehicleConditions.map((condition, index) => (
              <div className="mb-3">
                <li
                  key={index}
                  className="text-light li-no-style"
                >
                  <span className="offcanvas-condition-name">
                    {" "}
                    {capitalizeFirstLetter(condition.name) + " : "}{" "}
                  </span>{" "}
                  <br />
                  <span className="offcanvas-condition-description">
                    {condition.description}
                  </span>
                </li>
                <hr />
              </div>
            ))}
          </ul>
        </Offcanvas.Body>
      </Offcanvas>

      <Offcanvas
        show={showOffcanvasRequest}
        onHide={handleClose}
        placement="left"
      >
        <Offcanvas.Header closeButton>
          <Offcanvas.Title>Offcanvas en bas</Offcanvas.Title>
        </Offcanvas.Header>
        <Offcanvas.Body>
          <Form className="text-light px-1 py-3">
            <div className="mb-3">
              <Form.Check
                inline
                label="M."
                name="gender"
                type="radio"
                id={`inline-radio-mr`}
              />
              <Form.Check
                inline
                label="Mme."
                name="gender"
                type="radio"
                id={`inline-radio-md`}
              />

              <Form.Group className="mb-3">
                <Form.Label>Nom</Form.Label>
                <Form.Control
                  type="text"
                  placeholder="Nom"
                  required
                />

                <Form.Label>Adresse Email</Form.Label>
                <Form.Control
                  type="email"
                  placeholder="adresse@mail.fr"
                />

                <Form.Label>Numéro de téléphone</Form.Label>
                <Form.Control
                  type="phone"
                  value={phoneNumber}
                  onChange={handlePhoneChange}
                  required
                />
                <Form.Control.Feedback type="invalid">
                  Entrez un numero de téléphone valide
                </Form.Control.Feedback>
              </Form.Group>

              <Form.Check
                inline
                label="Préfèrer être contacté par email"
                name="gender"
                type="checkbox"
                id={`inline-checkbox-md`}
              />

              <Form.Control
                type="text"
                placeholder="Réfèrence du véhicule"
                aria-label="Réfèrence du véhicule"
                disabled
                readOnly
              />

              <Form.Group
                className="mb-3"
                controlId="exampleForm.ControlTextarea1"
              >
                <Form.Label>Message</Form.Label>
                <Form.Control
                  as="textarea"
                  rows={5}
                />
              </Form.Group>
            </div>
          </Form>
        </Offcanvas.Body>
      </Offcanvas>

      
    </>
  );
}

export default StickyDetails;
