import { Container } from 'react-bootstrap';
import { addNewTestimony } from '../serverRelated/ApiRequest';
import Form from 'react-bootstrap/Form';

export const UserLeaveATestimony = () =>
{
    
    return (
      <div className='bg-dark p-5'>
        <Container>
        <Form.Group className="mb-3" controlId="name">
        <Form.Label>Votre nom</Form.Label>
        <Form.Control type="text" placeholder="" />
      </Form.Group>
        <Form.Group className="mb-3" controlId="testimony">
        <Form.Label>Qu'avez vous pensé de votre expérience au garage Vincent Parrot ?</Form.Label>
        <Form.Control as="textarea" rows={3} />
      </Form.Group>
      <Form.Group className="mb-3" controlId="exampleForm.ControlTextarea">
        <Form.Label className=''>Donnez nous une note de 1 à 5</Form.Label>
        <Form.Control as="number"/>
        </Form.Group>

      </Container>
      </div>      
      );
}

export default UserLeaveATestimony;
