import React, { useState } from 'react';
import Button from 'react-bootstrap/Button';
import Form from 'react-bootstrap/Form';
import axios from 'axios'; // Assurez-vous d'installer axios avec `npm install axios`

function AdminConnexionPannel() {
  const [email, setEmail] = useState(''); // État pour l'email
  const [password, setPassword] = useState(''); // État pour le mot de passe

  const handleSubmit = async (event) => {
    event.preventDefault(); // Empêche le comportement par défaut du formulaire

    try {
      // Remplacer 'url_de_votre_api' par l'URL de votre API
      const response = await axios.post('http://localhost:5001/api/admin/login', { email, password });
      console.log(response.data); // Traiter la réponse ici
      // Vous pouvez rediriger l'utilisateur ou afficher un message de succès
    } catch (error) {
      console.error("Erreur de connexion : ", error.response ? error.response.data : error.message);
      // Afficher un message d'erreur à l'utilisateur
    }
  };

  return (
    <Form onSubmit={handleSubmit}> {/* Utilisation de l'événement onSubmit pour le formulaire */}
      <Form.Group className="mb-3" controlId="formBasicEmail">
        <Form.Label>Email address</Form.Label>
        <Form.Control
          type="email"
          placeholder="Enter email"
          value={email}
          onChange={(e) => setEmail(e.target.value)} // Mettre à jour l'état de l'email
        />
        <Form.Text className="text-muted">
          We'll never share your email with anyone else.
        </Form.Text>
      </Form.Group>

      <Form.Group className="mb-3" controlId="formBasicPassword">
        <Form.Label>Password</Form.Label>
        <Form.Control
          type="password"
          placeholder="Password"
          value={password}
          onChange={(e) => setPassword(e.target.value)} // Mettre à jour l'état du mot de passe
        />
      </Form.Group>
      <Button variant="primary" type="submit"> {/* Le type est "submit" pour soumettre le formulaire */}
        Submit
      </Button>
    </Form>
  );


  return (
    <Form onSubmit={handleSubmit}> {/* Ajout de l'événement onSubmit ici */}
      <Form.Group className="mb-3" controlId="formBasicEmail">
        <Form.Label>Email address</Form.Label>
        <Form.Control
          type="email"
          placeholder="Enter email"
          value={email}
          onChange={(e) => setEmail(e.target.value)} // Mettre à jour l'état de l'email
        />
        <Form.Text className="text-muted">
          We'll never share your email with anyone else.
        </Form.Text>
      </Form.Group>

      <Form.Group className="mb-3" controlId="formBasicPassword">
        <Form.Label>Password</Form.Label>
        <Form.Control
          type="password"
          placeholder="Password"
          value={password}
          onChange={(e) => setPassword(e.target.value)} // Mettre à jour l'état du mot de passe
        />
      </Form.Group>
      <Button variant="primary" type="submit" onClick={handleConnexion}> {/* Le type doit être "submit" pour soumettre le formulaire */}
        Submit
      </Button>
    </Form>
  );
}

export default AdminConnexionPannel;
